$(document).ready(function(){$("button").click(function(){var e=$("input").val();$.simpleWeather({zipcode:e,unit:"f",success:function(e){html="<h2>"+e.temp+"&deg;"+e.units.temp+"</h2>";html+="<ul><li>"+e.city+", "+e.region+"</li>";html+='<li class="currently">'+e.currently+"</li>";html+="<li>"+e.tempAlt+"&deg;c</li></ul>";$("#weather").html(html)},error:function(e){$("#weather").html("<p>"+e+"</p>")}})})});