$(document).ready(function(){$(".changeloc").hide();$("#button").click(function(){var e=$("input").val();$(".enterzip").hide();$("#weather").show();$.simpleWeather({zipcode:e,unit:"f",success:function(e){html="<h2>"+e.temp+"&deg;"+e.units.temp+"</h2>";html+="<ul><li>"+e.city+", "+e.region+"</li>";html+='<li class="currently">'+e.currently+"</li>";$("#weather").html(html)},error:function(e){$("#weather").html("<p>"+e+"</p>")}});$(".changeloc").show()});$(".changeloc").click(function(){$("#weather").hide();$(".enterzip").show();$(".changeloc").hide()})});