$(document).ready(function(){"standalone"in navigator&&!navigator.standalone&&/iphone|ipod|ipad/gi.test(navigator.platform)&&/Safari/i.test(navigator.appVersion)&&$("p.ios").show();$(".changeloc").hide();$("#button").click(function(){var e=$("input.zip").val(),t=$("input.cloc").val();$(".enterzip").hide();$("#weather").show();$.simpleWeather({zipcode:e,unit:"f",location:t,woeid:t,success:function(e){html="<h1>"+e.city+", "+e.region+"</h1>";html+='<p><span class="weather-'+e.code+'" >'+"</span>"+"</p>";html+="<ul><li>"+e.temp+"&deg;"+e.units.temp+"</li>";html+="<li>"+e.currently+"</li></ul>";html+='<ul class="weatherforcast"><li>Today</li><li>High '+e.high+"&deg;F"+"</li>"+"<li>"+"Low "+e.low+"&deg;F"+"</li></ul>";html+='<ul class="weatherforcast"><li>Tomorrow</li><li>High '+e.tomorrow.high+"&deg;F"+"</li>"+"<li>"+"Low "+e.tomorrow.low+"&deg;F"+"</li></ul>";$("#weather").html(html);$("#weather2").html(html)},error:function(e){$("#weather").html("<p>"+e+"</p>")}});$(".changeloc").show()});$(".changeloc").click(function(){$("#weather").hide();$(".enterzip").show();$(".changeloc").hide()})});